import React from "react";
import {
  collection,
  addDoc,
  doc,
  setDoc,
  getDocs,
  getDoc,
  query,
  orderBy,
  limit,
} from "firebase/firestore";
import { useState, useEffect } from "react";
import { db } from "../firebase";
export default function Publish(props) {
  const [result, setResult] = useState("");

  useEffect(() => {
    async function publish() {
      const subColRef = query(
        collection(db, "Admin", props.electionId, "candidates"),
        orderBy("NoOfVotes", "desc"),
        limit(1)
      );

      const querySnapshot = await getDocs(subColRef);
      querySnapshot.forEach((doc) => {
        // doc.data() is never undefined for query doc snapshots
        console.log(doc.data());
        setResult(doc.data());
      });
    }
    publish();
  }, []);

  console.log(result);
  return (
    <div>
      <div className="grid grid-cols-3 gap-10">
        {result.map((post) => {
          return (
            <div className="bg-[#333]  mt-5 p-10 rounded-md">
              <h1 className="text-2xl text-gray-300">{post.Name}</h1>
              <h1 className="text-2xl text-gray-300">{post.Party}</h1>
              <p className="text-gray-400 my-6">{post.Party}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
}
