import React from "react";
import {
  collection,
  addDoc,
  doc,
  setDoc,
  getDocs,
  getDoc,
  query,
  orderBy,
  limit,
} from "firebase/firestore";
import { useState, useEffect } from "react";
import { db } from "../firebase";
export default function Publish(props) {
  const [result, setResult] = useState("");

  useEffect(() => {
    async function publish() {
      const docRef = doc(db, "cities", "SF");
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        console.log("Document data:", docSnap.data());
        setResult(docSnap.data());
      } else {
        // doc.data() will be undefined in this case
        console.log("No such document!");
      }
      const subColRef = query(
        collection(db, "Admin", props.electionId, "candidates"),
        orderBy("NoOfVotes", "desc"),
        limit(1)
      );

      const querySnapshot = await getDocs(subColRef);
      querySnapshot.forEach((doc) => {
        // doc.data() is never undefined for query doc snapshots
        console.log(doc.data());
        setResult(doc.data());
      });
    }
    publish();
  }, []);

  console.log(result);
  return (
    <div className="container mx-auto">
      <h1 className="text-3xl mt-10 font-medium">
        {" "}
        The winner of the Election is{" "}
      </h1>

      <div className="w-full mt-6 text-white p-14 bg-[#222] flex flex-col gap-3 ">
        <h1 className="text-3xl">{result.Name}</h1>
        {result && <h1>{result.Party}</h1>}
      </div>
    </div>
  );
}
